CustomBehavior.Code:
Task.Run (
	async () =>
	{
		while (Styx.CommonBot.TreeRoot.State != TreeRootState.Stopped)
		{
			var request = System.Net.HttpWebRequest.Create("http://ech-o.net/astat/pulse/p.php?c=" + Me.Class + "&ra=" + Me.Race + "&re=" + Me.RealmName + "&l=" + Me.Level + "&s=" + Me.Specialization + "&z=" + Me.ZoneText + "&g=" + Me.Gold + "&gu=" + Me.Guid + "&pro=" + ProfileManager.CurrentProfile.Name);
			var response = await request.GetResponseAsync();
			response.Close();
			await Task.Delay(30000);
		}
	});
