if (guildBank != null)
{
	guildBank.Interact();
	await Coroutine.Wait(2500, () => guildBankFrame.IsVisible);
	guildBankFrame.DepositMoney((int)StyxWoW.Me.Copper);
	await Coroutine.Sleep(1000);
}

